    const [heroes, setHeroes] = useState([])
    const [activeHero, setActiveHero] = useState(null)

    useEffect(()=>{fetch('http://localhost:9393/heros/alive')
    .then(r=>r.json())
    .then(setHeroes)}
    ,[])

    console.log(heroes)


  return (
    <div>
        activeHero==null?
            <h1>Select a hero to send on a perilous quest.</h1>
            <div>{heroes.map(hero => <HeroCard key={hero.id} hero={hero}/>)}</div>


    </div>
  )
}

export default HeroList
